import { useState } from "react";
import styled, { keyframes } from "styled-components";
import Link from "next/link";

import FacebookIcon from "../../assets/icons/facebook.svg";
import YoutubeIcon from "../../assets/icons/youtube.svg";
import InstagramIcon from "../../assets/icons/instagram.svg";
import MailIcon from "../../assets/icons/mail.svg";
import PhoneIcon from "../../assets/icons/phone.svg";
import PinIcon from "../../assets/icons/pin.svg";

import { validateEmail } from "../../utils";

import PlainAnchor from "../../components/PlainAnchor";

const A = PlainAnchor;

const Flow = keyframes`
  to {
    background-position: 200% center;
  }
`;

const Footer = styled.footer`
  padding: 100px 100px 20px 100px;
  font-family: var(--font-secondary);
  font-size: 14px;
  color: var(--color-text);

  & > div + div {
    margin-top: 50px;
  }

  @media (max-width: 1200px) {
    padding: 100px 75px 20px 75px;
  }

  @media (max-width: 896px) {
    padding: 75px 50px 20px 50px;

    & > div + div {
      margin-top: 0;
    }
  }

  @media (max-width: 600px) {
    padding: 50px 20px 20px 20px;
    font-size: 12px;
  }
`;

const SmallGreyText = styled.p`
  font-size: 12px;
  color: var(--color-grey);

  @media (max-width: 600px) {
    font-size: 10px;
  }
`;

const PrimaryRow = styled.div`
  display: flex;
  flex-direction: horizontal;
  justify-content: space-between;

  @media (max-width: 896px) {
    flex-wrap: wrap;

    & > * {
      margin-bottom: 50px;
    }

    & > *:last-child {
      margin-bottom: 20px;
    }

    & > *:not(:last-child) {
      margin-right: 30px;
    }
  }
`;

const SecondaryRow = styled.div`
  display: flex;
  flex-direction: horizontal;
  justify-content: space-between;
  align-items: center;

  @media (max-width: 896px) {
    flex-wrap: wrap-reverse;

    & > *:first-child {
      margin-bottom: 20px;
    }

    & > *:last-child {
      margin-bottom: 50px;
    }

    & > *:not(:last-child) {
      margin-right: 50px;
    }
  }
`;

const TertiaryRow = styled.div`
  font-size: 12px;
  color: var(--color-grey);

  @media (max-width: 600px) {
    font-size: 10px;
  }
`;

const LinkColumn = styled.ul`
  & li:first-child {
    color: var(--color-grey);
  }

  & > li + li {
    margin-top: 10px;
  }
`;

const SocialsColumn = styled.div`
  & > * + * {
    margin-top: 10px;
  }
`;

const SubscribeGroup = styled.div`
  position: relative;
`;

const SubscribeInput = styled.input`
  font-family: var(--font-secondary);
  font-size: 14px;
  color: var(--color-background);
  width: 350px;
  padding: 10px 100px 10px 8px;
  border-radius: 5px;
  background-color: var(--color-text);
  border: none;
  margin-top: 10px;
  outline: ${(props) => (props.invalid ? "2px solid red" : null)};

  @media (max-width: 600px) {
    font-size: 12px;
    width: 320px;
  }
`;

const SubscribeError = styled.p`
  display: flex;
  align-items: flex-end;
  color: red;
  padding-left: 8px;
  height: 30px;
`;

const SubscribeButton = styled.button`
  position: absolute;
  right: 0;
  bottom: 30px;
  padding: 10px 20px;
  border-radius: 0 5px 5px 0;
  font-family: var(--font-primary);
  font-weight: 700;
  background: rgb(129, 5, 216);
  background: linear-gradient(
    140deg,
    var(--color-gradient-one) 20%,
    var(--color-gradient-two) 40%,
    var(--color-gradient-two) 60%,
    var(--color-gradient-one) 80%
  );
  background-size: 200% auto;
  animation: ${Flow} 2s linear infinite;

  @media (max-width: 600px) {
    padding: 10.5px 20px;
  }

  &:hover {
    animation: ${Flow} 0.3s linear infinite;
  }
`;

const SocialsIconGroup = styled.div`
  display: flex;

  & > a + a {
    margin-left: 20px;
  }
`;

const LogosColumn = styled.div``;

const LogosGroup = styled.div`
  display: flex;
  margin-top: 20px;

  & img {
    height: 70px;
  }

  & a + a {
    margin-left: 20px;
  }
`;

const ContactsColumn = styled.ul`
  width: 350px;

  @media (max-width: 600px) {
    width: 320px;
  }

  & > li {
    display: flex;
    align-items: center;
  }

  & > li + li {
    margin-top: 10px;
  }

  & > li > svg + p {
    margin-left: 10px;
  }
`;

const FooterSection = () => {
  const [subscribeVal, setSubscribeVal] = useState("");
  const [subscribeError, setSubscribeError] = useState("");

  const onSubscribeSubmit = () => {
    if (subscribeVal) {
      if (validateEmail(subscribeVal)) {
        setSubscribeError("");
        setSubscribeVal("");
        window.open(
          `https://esplanade.us16.list-manage.com/subscribe?u=de0574ea85b2a96b18407e8d7&id=abb3f82f99&MERGE0=${subscribeVal}`
        );
      } else {
        setSubscribeError("Your email is invalid.");
      }
    } else {
